mapper.select:
select "userNo","userId","userNm","userCellNum",
(select "value" as ${info} from member_info_value where "userNo"=#{userNo} and "attr"=#{info})
from member where "userNo"=#{userNo}

mapper.delete:
delete from member_info_value
where "userNo"=#{userNo};
delete from member
where "userNo"=#{userNo}
