mapper.update:
update recordbooks_value
set value=#{value}
where bookno=(select bookno from recordbooks where booknm=#{bookNm}) and
      userno=#{userNo} and
      bookcolumnno=#{columnNo}

mapper.select:
select case when count(userno)>0 then 1 else 0 end
from recordbooks_value
where userno=#{userno}
